import Vue from "vue";
import VueRouter from "vue-router";
Vue.use(VueRouter);

//导入组件
import Home from '@/components/main.vue'
import Submit from '@/components/submit.vue'
// import Login from '@/components/12-vue路由/Login.vue'
// import Register from '@/components/12-vue路由/Register.vue'
// import mycss from '@/css/mycss.css'

const router = new VueRouter({// 生成一个路由实例
    routes : [ //路由表示的是url和组件的一一对应的关系
        {
            //为路径设置重定向
            path:"/",
            redirect: "/main"
        },
        {
            name:'home', // 给当前路由组件定义的名字
            path:'/application/home', // url路径
            component:Home, // 组件名称
        },
        {
            name:'submit',
            path:'/application/submit',
            component:Submit
        },
        
    ],
        // linkActiveClass:mycss,
});
    export default router // 导出路由实例
