import Vue from "vue";
import VueRouter from "vue-router";
Vue.use(VueRouter);

//导入组件
// import Home from '@/components/12-vue路由/Home.vue'
// import Login from '@/components/12-vue路由/Login.vue'
// import Register from '@/components/12-vue路由/Register.vue'
// import mycss from '@/css/mycss.css'

import Home from '@/view/home/Index.vue'
import Login from '@/view/home/Login.vue'
import Register from '@/view/home/Register.vue'

const router = new VueRouter({// 生成一个路由实例
    routes : [ //路由表示的是url和组件的一一对应的关系
        {
            //为路径设置重定向
            path:"/",
            redirect: "/index"
        },
        {
            name:'home', // 给当前路由组件定义的名字
            path:'/home', // url路径
            component:Home, // 组件名称
        },
        {
            name:'login',
            path:'/login',
            component:Login
        },
        {
            name:'register',
            path:'/register',
            component:Register
        },
        {
            path:'/',
            component:Register
        },
    ],
        // linkActiveClass:mycss,
});
    export default router // 导出路由实例
