      //      lineChart.setOption(lineoption);
      //   })
      // }

      // 更新每个仓库的数据
      lineoption.series.forEach((series, index) => {
        // 字段名：warehouse1, warehouse2...
        const field = `warehouse${index + 1}`;
        series.data = res.data.map(v => v[field] || 0);
      });

      // 重新渲染图表
      lineChart.setOption(lineoption);
    }).catch(error => {
      console.error('获取图表数据失败:', error);
    });
  }
};
