import Vue from "vue";
import VueRouter from "vue-router";
Vue.use(VueRouter);

//导入组件
import application_in from '@/components/submit.vue'
import application_out from '@/components/submit.vue'
import application_move from '@/components/submit.vue'
import manager_in_search from '@/components/submit.vue'
import manager_in_view from '@/components/submit.vue'
import manager_out_search from '@/components/submit.vue'
import manager_out_view from '@/components/submit.vue'

const router = new VueRouter({// 生成一个路由实例
    routes : [ //路由表示的是url和组件的一一对应的关系
        {
            //为路径设置重定向
            path:"/",
            redirect: "/main"
        },
        {//1-1
            name:'in', // 给当前路由组件定义的名字
            path:'/application/in', // url路径
            component:application_in, // 组件名称
        },
        {//1-2
            name:'out',
            path:'/application/out',
            component:application_out
        },
        {//1-3
            name:'move',
            path:'/application/move',
            component:application_move
        },

        {//2-1
            name:'search',
            path:'/manager_in/search',
            component:manager_in_search
        },
        {//2-2
            name:'view',
            path:'/manager_in/view',
            component:manager_in_view
        },

        {//3-1
            name:'search',
            path:'/manager_out/search',
            component:manager_out_search
        },
        {//3-2
            name:'view',
            path:'/manager_out/view',
            component:manager_out_view
        },
        
    ],
        // linkActiveClass:mycss,
});
    export default router // 导出路由实例
